export function AdminsPage() {
  const navigate = useNavigate();
  const { toast } = useToast();
  const { user, hasRole } = useAuth();

  const [searchQuery, setSearchQuery] = useState('');
  const [roleFilter, setRoleFilter] = useState<string>('all');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [editingAdmin, setEditingAdmin] = useState<User | null>(null);
  const [editForm, setEditForm] = useState({ name: '', email: '', role: 'admin', isActive: true });
  const [editErrors, setEditErrors] = useState<Record<string, string>>({});

  // Use CRUD hook for admin management
  const {
    items: admins,
    loading,
    deleting,
    updating,
    fetchItems,
    updateItem,
    deleteItem,
    setFilters,
  } = useCRUD<User>({
    resource: 'admins',
    api: api.admins,
    messages: {
      updated: 'Admin updated successfully',
      deleted: 'Admin deleted successfully',
    },
  });

  // Apply filters when search or filter values change
  useEffect(() => {
    const filters: any = {};

    if (searchQuery.trim()) {
      filters.search = searchQuery.trim();
    }

    if (roleFilter !== 'all') {
      filters.role = roleFilter;
    }

    if (statusFilter !== 'all') {
      filters.isActive = statusFilter === 'active';
    }

    setFilters(filters);
  }, [searchQuery, roleFilter, statusFilter, setFilters]);

  // Filter admins based on search query
  const filteredAdmins = admins;

  const handleDeleteAdmin = async (adminId: string) => {
    const adminToDelete = admins.find(a => a.id === adminId);

    // Prevent deleting self
    if (adminToDelete?.email === user?.email) {
      toast({
        title: 'Cannot delete yourself',
        description: 'You cannot delete your own admin account',
        variant: 'destructive',
      });
      return;
    }

    await deleteItem(adminId);
  };

  const handleEditAdmin = (admin: User) => {
    setEditingAdmin(admin);
    setEditForm({ 
      name: admin.name, 
      email: admin.email, 
      role: admin.role,
      isActive: admin.isActive ?? true 
    });
    setEditErrors({});
    setIsEditDialogOpen(true);
  };

  const validateEditForm = (): boolean => {
    const newErrors: Record<string, string> = {};
    if (!editForm.name.trim()) newErrors.name = 'Name is required';
    if (!editForm.email.trim()) newErrors.email = 'Email is required';
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(editForm.email)) newErrors.email = 'Invalid email format';

    setEditErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleUpdateAdmin = async () => {
    if (!validateEditForm() || !editingAdmin) return;

    const updateData = {
      name: editForm.name.trim(),
      email: editForm.email.trim(),
      role: editForm.role,
      isActive: editForm.isActive,
    };

    const result = await updateItem(editingAdmin.id, updateData);

    if (result) {
      setIsEditDialogOpen(false);
      setEditingAdmin(null);
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getRoleBadge = (role: string) => {
    if (role === 'superadmin') {
      return (
        <Badge variant="default" className="gap-1">
          <ShieldCheck className="h-3 w-3" />
          Super Admin
        </Badge>
      );
    }
    return (
      <Badge variant="secondary" className="gap-1">
        <Shield className="h-3 w-3" />
        Admin
      </Badge>
    );
  };

  const getStatusBadge = (isActive?: boolean) => {
    return isActive ? (
      <Badge variant="default">Active</Badge>
    ) : (
      <Badge variant="secondary">Inactive</Badge>
    );
  };
  // Only super admins can access this page
  if (!hasRole('superadmin')) {
    return (
      <div className="space-y-6">
        <div>
          <h1 className="text-3xl font-bold">Access Denied</h1>
          <p className="text-muted-foreground">You don't have permission to access this page</p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold">Admins</h1>
            <p className="text-muted-foreground">Manage admin users and their permissions</p>
          </div>
        </div>
        <Card>
          <CardContent className="pt-6">
            <div className="space-y-4">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-12 bg-muted rounded animate-pulse" />
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }
  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Admins</h1>
          <p className="text-muted-foreground">Manage admin users and their permissions</p>
        </div>
        <Button onClick={() => navigate('/admins/create')} className="gap-2">
          <Plus className="h-4 w-4" />
          Add Admin
        </Button>
      </div>

      {/* Search and Filters */}
      <Card>
        <CardHeader>
          <CardTitle>Admin Management</CardTitle>
          <CardDescription>
            View and manage all admin users in the system
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex items-center gap-4 mb-6">
            <div className="relative flex-1 max-w-sm">
              <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Search admins..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-9"
              />
            </div>
            <Select value={roleFilter} onValueChange={setRoleFilter}>
              <SelectTrigger className="w-40">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Roles</SelectItem>
                <SelectItem value="superadmin">Super Admin</SelectItem>
                <SelectItem value="admin">Admin</SelectItem>
              </SelectContent>
            </Select>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-40">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="active">Active</SelectItem>
                <SelectItem value="inactive">Inactive</SelectItem>
              </SelectContent>
            </Select>
            <div className="text-sm text-muted-foreground">
              {filteredAdmins.length} of {admins.length} admins
            </div>
          </div>

          {/* Admins Table */}
          <div className="border rounded-lg">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Email</TableHead>
                  <TableHead>Role</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Last Login</TableHead>
                  <TableHead>Created</TableHead>
                  <TableHead className="w-12"></TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredAdmins.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center py-8">
                      <div className="text-muted-foreground">
                        {searchQuery ? 'No admins found matching your search' : 'No admins found'}
                      </div>
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredAdmins.map((admin) => (
                    <TableRow key={admin.id}>
                      <TableCell className="font-medium">{admin.name}</TableCell>
                      <TableCell>{admin.email}</TableCell>
                      <TableCell>{getRoleBadge(admin.role)}</TableCell>
                      <TableCell>{getStatusBadge(admin.isActive)}</TableCell>
                      <TableCell>
                        {admin.lastLogin ? formatDate(admin.lastLogin) : 'Never'}
                      </TableCell>
                      <TableCell>{formatDate(admin.createdAt)}</TableCell>
                      <TableCell>
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" size="sm">
                              <MoreHorizontal className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem onClick={() => handleEditAdmin(admin)}>
                              <Edit className="mr-2 h-4 w-4" />
                              Edit
                            </DropdownMenuItem>
                            <DropdownMenuSeparator />
                            <AlertDialog>
                              <AlertDialogTrigger asChild>
                                <DropdownMenuItem 
                                  className="text-destructive"
                                  onSelect={(e) => e.preventDefault()}
                                  disabled={admin.email === user?.email}
                                >
                                  <Trash2 className="mr-2 h-4 w-4" />
                                  Delete
                                </DropdownMenuItem>
                              </AlertDialogTrigger>
                              <AlertDialogContent>
                                <AlertDialogHeader>
                                  <AlertDialogTitle>Delete Admin</AlertDialogTitle>
                                  <AlertDialogDescription>
                                    Are you sure you want to delete <strong>{admin.name}</strong>? 
                                    This action cannot be undone and will remove all admin privileges.
                                  </AlertDialogDescription>
                                </AlertDialogHeader>
                                <AlertDialogFooter>
                                  <AlertDialogCancel>Cancel</AlertDialogCancel>
                                  <AlertDialogAction
                                    onClick={() => handleDeleteAdmin(admin.id)}
                                    className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                                    disabled={deleting}
                                  >
                                    Delete Admin
                                  </AlertDialogAction>
                                </AlertDialogFooter>
                              </AlertDialogContent>
                            </AlertDialog>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      {/* Edit Admin Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Edit Admin</DialogTitle>
            <DialogDescription>
              Update admin details and permissions
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="edit-name">Name *</Label>
              <Input
                id="edit-name"
                value={editForm.name}
                onChange={(e) => setEditForm(prev => ({ ...prev, name: e.target.value }))}
                className={editErrors.name ? 'border-destructive' : ''}
              />
              {editErrors.name && <p className="text-sm text-destructive">{editErrors.name}</p>}
            </div>

            <div className="space-y-2">
              <Label htmlFor="edit-email">Email *</Label>
              <Input
                id="edit-email"
                type="email"
                value={editForm.email}
                onChange={(e) => setEditForm(prev => ({ ...prev, email: e.target.value }))}
                className={editErrors.email ? 'border-destructive' : ''}
              />
              {editErrors.email && <p className="text-sm text-destructive">{editErrors.email}</p>}
            </div>

            <div className="space-y-2">
              <Label htmlFor="edit-role">Role *</Label>
              <Select value={editForm.role} onValueChange={(value) => setEditForm(prev => ({ ...prev, role: value }))}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="admin">Admin</SelectItem>
                  <SelectItem value="superadmin">Super Admin</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="flex items-center space-x-2">
              <Switch
                id="edit-active"
                checked={editForm.isActive}
                onCheckedChange={(checked) => setEditForm(prev => ({ ...prev, isActive: checked }))}
              />
              <Label htmlFor="edit-active">Active</Label>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsEditDialogOpen(false)}>
              Cancel
            </Button>
            <Button onClick={handleUpdateAdmin} disabled={updating}>
              Update Admin
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

export default AdminsPage;

import React, { useState, useEffect } from 'react';
import { Check, X, Clock, AlertCircle, User, Briefcase, Building2, Eye, MessageSquare } from 'lucide-react';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { useToast } from '@/hooks/use-toast';

import useCRUD from '../hooks/useCRUD';
import api from '../lib/api';

interface ApprovalRequest {
  id: string;
  type: 'provider' | 'job' | 'blog' | 'category';
  title: string;
  description: string;
  submitter: {
    name: string;
    email: string;
    avatar?: string;
  };
  status: 'pending' | 'approved' | 'rejected';
  priority: 'low' | 'medium' | 'high';
  submittedAt: string;
  details: any;
  notes?: string;
}


export function ApprovalsPage() {
  const { toast } = useToast();

  const [approvalStats, setApprovalStats] = useState({
    pending: 0,
    approved: 0,
    rejected: 0,
    total: 0
  });
  const [selectedApproval, setSelectedApproval] = useState<ApprovalRequest | null>(null);
  const [detailsDialogOpen, setDetailsDialogOpen] = useState(false);
  const [actionDialogOpen, setActionDialogOpen] = useState(false);
  const [actionType, setActionType] = useState<'approve' | 'reject'>('approve');
  const [actionNotes, setActionNotes] = useState('');

  // Use CRUD hook for approvals management
  const {
    items: allApprovals,
    loading,
    fetchItems,
    setFilters,
  } = useCRUD<ApprovalRequest>({
    resource: 'approvals',
    api: api.approvals,
  });

  // Separate state for different approval statuses
  const [pendingApprovals, setPendingApprovals] = useState<ApprovalRequest[]>([]);
  const [approvedApprovals, setApprovedApprovals] = useState<ApprovalRequest[]>([]);
  const [rejectedApprovals, setRejectedApprovals] = useState<ApprovalRequest[]>([]);

  useEffect(() => {
    fetchApprovalData();
  }, []);

  const fetchApprovalData = async () => {
    try {
      // Fetch pending approvals
      const pendingResponse = await api.approvals.getPending();
      if (pendingResponse.success) {
        setPendingApprovals(pendingResponse.data);
      }

      // Fetch all approvals for approved/rejected lists
      const allResponse = await api.approvals.list();
      if (allResponse.success) {
        const approved = allResponse.data.filter((a: ApprovalRequest) => a.status === 'approved');
        const rejected = allResponse.data.filter((a: ApprovalRequest) => a.status === 'rejected');
        setApprovedApprovals(approved);
        setRejectedApprovals(rejected);
      }

      // Fetch approval statistics
      const statsResponse = await api.approvals.getStats();
      if (statsResponse.success) {
        setApprovalStats(statsResponse.data);
      }
    } catch (error: any) {
      console.error('Failed to fetch approval data:', error);
      toast({
        title: 'Error',
        description: 'Failed to load approval data. Using offline mode.',
        variant: 'destructive',
      });

      // Fallback to mock data if API fails
      const mockApprovals = [
        {
          id: '1',
          type: 'provider' as const,
          title: 'Pacific Marine Solutions',
          description: 'New provider registration for marine equipment and services',
          submitter: { name: 'John Smith', email: 'john@pacificmarine.com' },
          status: 'pending' as const,
          priority: 'high' as const,
          submittedAt: '2024-01-25T10:30:00Z',
          details: {
            companyName: 'Pacific Marine Solutions',
            location: 'Seattle, WA',
            services: ['Equipment Supply', 'Maintenance', 'Repair'],
            website: 'https://pacificmarine.com',
            employees: '25-50',
            experience: '15 years'
          }
        }
      ];
      setPendingApprovals(mockApprovals);
      setApprovalStats({ pending: 1, approved: 0, rejected: 0, total: 1 });
    }
  };

  const handleViewDetails = (approval: ApprovalRequest) => {
    setSelectedApproval(approval);
    setDetailsDialogOpen(true);
  };

  const handleApprovalAction = (approval: ApprovalRequest, action: 'approve' | 'reject') => {
    setSelectedApproval(approval);
    setActionType(action);
    setActionNotes('');
    setActionDialogOpen(true);
  };

  const handleSubmitAction = async () => {
    if (!selectedApproval) return;

    try {
      if (actionType === 'approve') {
        const response = await api.approvals.approve(selectedApproval.id, actionNotes.trim() || undefined);
        if (!response.success) {
          throw new Error(response.message || 'Failed to approve request');
        }
      } else {
        if (!actionNotes.trim()) {
          toast({
            title: 'Rejection reason required',
            description: 'Please provide a reason for rejecting this request',
            variant: 'destructive',
          });
          return;
        }

        const response = await api.approvals.reject(selectedApproval.id, actionNotes.trim());
        if (!response.success) {
          throw new Error(response.message || 'Failed to reject request');
        }
      }

      // Refresh approval data after action
      await fetchApprovalData();

      toast({
        title: `Request ${actionType === 'approve' ? 'approved' : 'rejected'}`,
        description: `${selectedApproval.title} has been ${actionType === 'approve' ? 'approved' : 'rejected'}`,
      });

      setActionDialogOpen(false);
      setSelectedApproval(null);
      setActionNotes('');
    } catch (error: any) {
      console.error('Approval action error:', error);
      toast({
        title: 'Error',
        description: error.message || 'Failed to process approval. Please try again.',
        variant: 'destructive',
      });
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'provider': return Building2;
      case 'job': return Briefcase;
      case 'blog': return MessageSquare;
      case 'category': return User;
      default: return AlertCircle;
    }
  };

  const getTypeBadge = (type: string) => {
    const variants = {
      provider: 'default',
      job: 'secondary',
      blog: 'outline',
      category: 'destructive'
    } as const;

    return <Badge variant={variants[type as keyof typeof variants] || 'outline'}>{type}</Badge>;
  };

  const getPriorityBadge = (priority: string) => {
    const variants = {
      high: 'destructive',
      medium: 'secondary',
      low: 'outline'
    } as const;

    return <Badge variant={variants[priority as keyof typeof variants] || 'outline'}>{priority}</Badge>;
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="secondary" className="gap-1"><Clock className="h-3 w-3" />Pending</Badge>;
      case 'approved':
        return <Badge variant="default" className="gap-1"><Check className="h-3 w-3" />Approved</Badge>;
      case 'rejected':
        return <Badge variant="destructive" className="gap-1"><X className="h-3 w-3" />Rejected</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const renderApprovalCard = (approval: ApprovalRequest) => {
    const TypeIcon = getTypeIcon(approval.type);

    return (
      <Card key={approval.id} className="cursor-pointer hover:shadow-md transition-shadow">
        <CardHeader className="pb-3">
          <div className="flex items-start justify-between">
            <div className="flex items-center gap-3">
              <div className="p-2 bg-muted rounded-lg">
                <TypeIcon className="h-4 w-4" />
              </div>
              <div>
                <CardTitle className="text-base">{approval.title}</CardTitle>
                <CardDescription className="text-sm">{approval.description}</CardDescription>
              </div>
            </div>
            <div className="flex flex-col gap-2">
              {getTypeBadge(approval.type)}
              {getPriorityBadge(approval.priority)}
            </div>
          </div>
        </CardHeader>
        <CardContent className="pt-0">
          <div className="space-y-3">
            <div className="flex items-center justify-between text-sm">
              <div className="flex items-center gap-2">
                <User className="h-3 w-3 text-muted-foreground" />
                <span>{approval.submitter.name}</span>
              </div>
              <span className="text-muted-foreground">{formatDate(approval.submittedAt)}</span>
            </div>

            <div className="flex items-center justify-between">
              {getStatusBadge(approval.status)}
              <div className="flex gap-2">
                <Button 
                  variant="outline" 
                  size="sm" 
                  onClick={() => handleViewDetails(approval)}
                  className="gap-1"
                >
                  <Eye className="h-3 w-3" />
                  Details
                </Button>
                {approval.status === 'pending' && (
                  <>
                    <Button 
                      variant="outline" 
                      size="sm" 
                      onClick={() => handleApprovalAction(approval, 'reject')}
                      className="gap-1"
                    >
                      <X className="h-3 w-3" />
                      Reject
                    </Button>
                    <Button 
                      size="sm" 
                      onClick={() => handleApprovalAction(approval, 'approve')}
                      className="gap-1"
                    >
                      <Check className="h-3 w-3" />
                      Approve
                    </Button>
                  </>
                )}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  };

  const renderApprovalDetails = (approval: ApprovalRequest) => {
    switch (approval.type) {
      case 'provider':
        return (
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label className="text-sm font-medium">Company Name</Label>
                <div className="text-sm">{approval.details.companyName}</div>
              </div>
              <div>
                <Label className="text-sm font-medium">Location</Label>
                <div className="text-sm">{approval.details.location}</div>
              </div>
              <div>
                <Label className="text-sm font-medium">Website</Label>
                <div className="text-sm">{approval.details.website}</div>
              </div>
              <div>
                <Label className="text-sm font-medium">Company Size</Label>
                <div className="text-sm">{approval.details.employees} employees</div>
              </div>
              <div>
                <Label className="text-sm font-medium">Experience</Label>
                <div className="text-sm">{approval.details.experience}</div>
              </div>
            </div>
            <div>
              <Label className="text-sm font-medium">Services</Label>
              <div className="flex flex-wrap gap-2 mt-1">
                {approval.details.services.map((service: string) => (
                  <Badge key={service} variant="outline">{service}</Badge>
                ))}
              </div>
            </div>
          </div>
        );

      case 'job':
        return (
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label className="text-sm font-medium">Job Title</Label>
                <div className="text-sm">{approval.details.jobTitle}</div>
              </div>
              <div>
                <Label className="text-sm font-medium">Location</Label>
                <div className="text-sm">{approval.details.location}</div>
              </div>
              <div>
                <Label className="text-sm font-medium">Salary Range</Label>
                <div className="text-sm">{approval.details.salaryRange}</div>
              </div>
              <div>
                <Label className="text-sm font-medium">Type</Label>
                <div className="text-sm">{approval.details.type}</div>
              </div>
            </div>
            <div>
              <Label className="text-sm font-medium">Requirements</Label>
              <ul className="text-sm list-disc list-inside mt-1">
                {approval.details.requirements.map((req: string, index: number) => (
                  <li key={index}>{req}</li>
                ))}
              </ul>
            </div>
            <div>
              <Label className="text-sm font-medium">Benefits</Label>
              <div className="flex flex-wrap gap-2 mt-1">
                {approval.details.benefits.map((benefit: string) => (
                  <Badge key={benefit} variant="outline">{benefit}</Badge>
                ))}
              </div>
            </div>
          </div>
        );

      case 'blog':
        return (
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label className="text-sm font-medium">Title</Label>
                <div className="text-sm">{approval.details.title}</div>
              </div>
              <div>
                <Label className="text-sm font-medium">Word Count</Label>
                <div className="text-sm">{approval.details.wordCount} words</div>
              </div>
              <div>
                <Label className="text-sm font-medium">Featured</Label>
                <div className="text-sm">{approval.details.featured ? 'Yes' : 'No'}</div>
              </div>
            </div>
            <div>
              <Label className="text-sm font-medium">Excerpt</Label>
              <div className="text-sm">{approval.details.excerpt}</div>
            </div>
            <div>
              <Label className="text-sm font-medium">Tags</Label>
              <div className="flex flex-wrap gap-2 mt-1">
                {approval.details.tags.map((tag: string) => (
                  <Badge key={tag} variant="outline">{tag}</Badge>
                ))}
              </div>
            </div>
          </div>
        );

      default:
        return <div className="text-sm text-muted-foreground">No additional details available</div>;
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Approvals</h1>
          <p className="text-muted-foreground">Review and manage pending approval requests</p>
        </div>
        <div className="flex items-center gap-4">
          <div className="text-sm text-muted-foreground">
            {pendingApprovals.length} pending approvals
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid gap-6 md:grid-cols-3">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Pending</CardTitle>
            <Clock className="h-4 w-4 text-orange-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-orange-600">{approvalStats.pending}</div>
            <p className="text-xs text-muted-foreground">Awaiting review</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Approved</CardTitle>
            <Check className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600">{approvalStats.approved}</div>
            <p className="text-xs text-muted-foreground">This month</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Rejected</CardTitle>
            <X className="h-4 w-4 text-red-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-red-600">{approvalStats.rejected}</div>
            <p className="text-xs text-muted-foreground">This month</p>
          </CardContent>
        </Card>
      </div>

      {/* Approval Lists */}
      <Tabs defaultValue="pending" className="space-y-6">
        <TabsList>
          <TabsTrigger value="pending">Pending ({approvalStats.pending})</TabsTrigger>
          <TabsTrigger value="approved">Approved ({approvalStats.approved})</TabsTrigger>
          <TabsTrigger value="rejected">Rejected ({approvalStats.rejected})</TabsTrigger>
