const MOCK_CATEGORIES = [
  { id: '1', name: 'Marine Equipment' },
  { id: '2', name: 'Boat Maintenance' },
  { id: '3', name: 'Electronics & Navigation' },
  { id: '4', name: 'Repair Services' },
  { id: '5', name: 'Marina Services' },
];

// Mock providers for selection
const MOCK_PROVIDERS = [
  { id: '1', name: 'Marine Solutions Ltd' },
  { id: '2', name: 'Ocean Tech Services' },
  { id: '3', name: 'Coastal Marine Works' },
];

export function CreateJobPage() {
  const navigate = useNavigate();
  const { toast } = useToast();

  const [isLoading, setIsLoading] = useState(false);
  const [formData, setFormData] = useState<CreateJobForm>({
    title: '',
    description: '',
    providerId: '',
    categoryIds: [],
    location: '',
    salaryRange: {
      min: 0,
      max: 0,
      currency: 'USD',
    },
    requirements: [],
    benefits: [],
    type: 'full-time',
    remote: false,
    urgency: 'medium',
    expiresAt: '',
  });

  const [currentRequirement, setCurrentRequirement] = useState('');
  const [currentBenefit, setCurrentBenefit] = useState('');
  const [errors, setErrors] = useState<Record<string, string>>({});

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    // Basic validation
    if (!formData.title.trim()) newErrors.title = 'Job title is required';
    if (!formData.description.trim()) newErrors.description = 'Job description is required';
    if (!formData.providerId) newErrors.providerId = 'Provider is required';
    if (!formData.location.trim()) newErrors.location = 'Location is required';
    if (formData.categoryIds.length === 0) newErrors.categoryIds = 'At least one category is required';
    if (formData.requirements.length === 0) newErrors.requirements = 'At least one requirement is required';
    if (formData.benefits.length === 0) newErrors.benefits = 'At least one benefit is required';
    if (!formData.expiresAt) newErrors.expiresAt = 'Expiration date is required';

    // Salary validation
    if (formData.salaryRange.min <= 0) newErrors.salaryMin = 'Minimum salary must be greater than 0';
    if (formData.salaryRange.max <= 0) newErrors.salaryMax = 'Maximum salary must be greater than 0';
    if (formData.salaryRange.min >= formData.salaryRange.max) {
      newErrors.salaryRange = 'Maximum salary must be greater than minimum salary';
    }

    // Date validation
    if (formData.expiresAt) {
      const expiryDate = new Date(formData.expiresAt);
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      if (expiryDate <= today) {
        newErrors.expiresAt = 'Expiration date must be in the future';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: string, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleSalaryChange = (field: 'min' | 'max', value: string) => {
    const numValue = parseFloat(value) || 0;
    setFormData(prev => ({
      ...prev,
      salaryRange: {
        ...prev.salaryRange,
        [field]: numValue,
      },
    }));
    if (errors[`salary${field.charAt(0).toUpperCase() + field.slice(1)}`]) {
      setErrors(prev => ({ ...prev, [`salary${field.charAt(0).toUpperCase() + field.slice(1)}`]: '' }));
    }
  };

  const handleAddRequirement = () => {
    if (currentRequirement.trim() && !formData.requirements.includes(currentRequirement.trim())) {
      setFormData(prev => ({
        ...prev,
        requirements: [...prev.requirements, currentRequirement.trim()],
      }));
      setCurrentRequirement('');
      if (errors.requirements) {
        setErrors(prev => ({ ...prev, requirements: '' }));
      }
    }
  };

  const handleRemoveRequirement = (requirement: string) => {
    setFormData(prev => ({
      ...prev,
      requirements: prev.requirements.filter(r => r !== requirement),
    }));
  };

  const handleAddBenefit = () => {
    if (currentBenefit.trim() && !formData.benefits.includes(currentBenefit.trim())) {
      setFormData(prev => ({
        ...prev,
        benefits: [...prev.benefits, currentBenefit.trim()],
      }));
      setCurrentBenefit('');
      if (errors.benefits) {
        setErrors(prev => ({ ...prev, benefits: '' }));
      }
    }
  };

  const handleRemoveBenefit = (benefit: string) => {
    setFormData(prev => ({
      ...prev,
      benefits: prev.benefits.filter(b => b !== benefit),
    }));
  };

  const handleCategoryToggle = (categoryId: string) => {
    setFormData(prev => ({
      ...prev,
      categoryIds: prev.categoryIds.includes(categoryId)
        ? prev.categoryIds.filter(id => id !== categoryId)
        : [...prev.categoryIds, categoryId],
    }));
    if (errors.categoryIds) {
      setErrors(prev => ({ ...prev, categoryIds: '' }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!validateForm()) return;
    setIsLoading(true);
    try {
      const res = await api.jobs.create(formData);
      if (!res.success) throw new Error(res.message || 'Failed to create job');
      toast({ title: 'Job posted successfully', description: `${formData.title} created` });
      navigate('/jobs');
    } catch (error: any) {
      toast({ title: 'Error creating job', description: error.message || 'Please try again later', variant: 'destructive' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleSaveAsDraft = async () => {
    setIsLoading(true);
    try {
      const draftData = { ...formData, status: 'draft' };
      const res = await api.jobs.create(draftData);
      if (!res.success) throw new Error(res.message || 'Failed to save draft');
      toast({ title: 'Job saved as draft', description: `${formData.title} saved` });
      navigate('/jobs');
    } catch (error: any) {
      toast({ title: 'Error saving draft', description: error.message || 'Please try again later', variant: 'destructive' });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center gap-4">
        <Button
          variant="ghost"
          size="sm"
          onClick={() => navigate('/jobs')}
          className="gap-2"
        >
          <ArrowLeft className="h-4 w-4" />
          Back to Jobs
        </Button>
        <div>
          <h1 className="text-3xl font-bold">Post New Job</h1>
          <p className="text-muted-foreground">Create a new job posting for marine professionals</p>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Basic Information */}
        <Card>
          <CardHeader>
            <CardTitle>Job Details</CardTitle>
            <CardDescription>
              Enter the basic information about the job position
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="title">Job Title *</Label>
                <Input
                  id="title"
                  placeholder="e.g., Marine Engineer"
                  value={formData.title}
                  onChange={(e) => handleInputChange('title', e.target.value)}
                  className={errors.title ? 'border-destructive' : ''}
                />
                {errors.title && <p className="text-sm text-destructive">{errors.title}</p>}
              </div>

              <div className="space-y-2">
                <Label htmlFor="provider">Provider *</Label>
                <Select value={formData.providerId} onValueChange={(value) => handleInputChange('providerId', value)}>
                  <SelectTrigger className={errors.providerId ? 'border-destructive' : ''}>
                    <SelectValue placeholder="Select provider" />
                  </SelectTrigger>
                  <SelectContent>
                    {MOCK_PROVIDERS.map((provider) => (
                      <SelectItem key={provider.id} value={provider.id}>
                        {provider.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                {errors.providerId && <p className="text-sm text-destructive">{errors.providerId}</p>}
              </div>

              <div className="space-y-2">
                <Label htmlFor="location">Location *</Label>
                <div className="relative">
                  <MapPin className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="location"
                    placeholder="e.g., Miami, FL"
                    value={formData.location}
                    onChange={(e) => handleInputChange('location', e.target.value)}
                    className={errors.location ? 'border-destructive pl-9' : 'pl-9'}
                  />
                </div>
                {errors.location && <p className="text-sm text-destructive">{errors.location}</p>}
              </div>

              <div className="space-y-2">
                <Label htmlFor="type">Job Type *</Label>
                <Select value={formData.type} onValueChange={(value) => handleInputChange('type', value)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="full-time">Full-time</SelectItem>
                    <SelectItem value="part-time">Part-time</SelectItem>
                    <SelectItem value="contract">Contract</SelectItem>
                    <SelectItem value="internship">Internship</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="urgency">Urgency Level</Label>
                <Select value={formData.urgency} onValueChange={(value) => handleInputChange('urgency', value)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="low">Low</SelectItem>
                    <SelectItem value="medium">Medium</SelectItem>
                    <SelectItem value="high">High</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="expires">Expires On *</Label>
                <div className="relative">
                  <Calendar className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="expires"
                    type="date"
                    value={formData.expiresAt}
                    onChange={(e) => handleInputChange('expiresAt', e.target.value)}
                    className={errors.expiresAt ? 'border-destructive pl-9' : 'pl-9'}
                  />
                </div>
                {errors.expiresAt && <p className="text-sm text-destructive">{errors.expiresAt}</p>}
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <Checkbox
                id="remote"
                checked={formData.remote}
                onCheckedChange={(checked) => handleInputChange('remote', checked)}
              />
              <Label htmlFor="remote">Remote work available</Label>
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">Job Description *</Label>
              <Textarea
                id="description"
                placeholder="Describe the job responsibilities, qualifications, and what makes this opportunity attractive..."
                rows={6}
                value={formData.description}
                onChange={(e) => handleInputChange('description', e.target.value)}
                className={errors.description ? 'border-destructive' : ''}
              />
              {errors.description && <p className="text-sm text-destructive">{errors.description}</p>}
            </div>
          </CardContent>
        </Card>

        {/* Salary Information */}
        <Card>
          <CardHeader>
            <CardTitle>Compensation</CardTitle>
            <CardDescription>
              Set the salary range for this position
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="space-y-2">
                <Label htmlFor="salary-min">Minimum Salary *</Label>
                <div className="relative">
                  <DollarSign className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="salary-min"
                    type="number"
                    placeholder="50000"
                    value={formData.salaryRange.min || ''}
                    onChange={(e) => handleSalaryChange('min', e.target.value)}
                    className={errors.salaryMin ? 'border-destructive pl-9' : 'pl-9'}
                  />
                </div>
                {errors.salaryMin && <p className="text-sm text-destructive">{errors.salaryMin}</p>}
              </div>

              <div className="space-y-2">
                <Label htmlFor="salary-max">Maximum Salary *</Label>
                <div className="relative">
                  <DollarSign className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="salary-max"
                    type="number"
                    placeholder="80000"
                    value={formData.salaryRange.max || ''}
                    onChange={(e) => handleSalaryChange('max', e.target.value)}
                    className={errors.salaryMax ? 'border-destructive pl-9' : 'pl-9'}
                  />
                </div>
                {errors.salaryMax && <p className="text-sm text-destructive">{errors.salaryMax}</p>}
              </div>

              <div className="space-y-2">
                <Label htmlFor="currency">Currency</Label>
                <Select
                  value={formData.salaryRange.currency}
                  onValueChange={(value) => setFormData(prev => ({
                    ...prev,
                    salaryRange: { ...prev.salaryRange, currency: value }
                  }))}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="USD">USD</SelectItem>
                    <SelectItem value="EUR">EUR</SelectItem>
                    <SelectItem value="GBP">GBP</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
            {errors.salaryRange && <p className="text-sm text-destructive">{errors.salaryRange}</p>}

            <div className="text-sm text-muted-foreground">
              {formData.type === 'part-time' ? 'Hourly rate' : 'Annual salary'}
            </div>
          </CardContent>
        </Card>

        {/* Categories */}
        <Card>
          <CardHeader>
            <CardTitle>Categories</CardTitle>
            <CardDescription>
              Select the categories that best match this job
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
              {MOCK_CATEGORIES.map((category) => (
                <div
                  key={category.id}
                  className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                    formData.categoryIds.includes(category.id)
                      ? 'bg-primary text-primary-foreground border-primary'
                      : 'bg-card hover:bg-muted border-border'
                  }`}
                  onClick={() => handleCategoryToggle(category.id)}
                >
                  <div className="text-sm font-medium">{category.name}</div>
                </div>
              ))}
            </div>
            {errors.categoryIds && <p className="text-sm text-destructive mt-2">{errors.categoryIds}</p>}
          </CardContent>
        </Card>

        {/* Requirements */}
        <Card>
          <CardHeader>
            <CardTitle>Requirements</CardTitle>
            <CardDescription>
              List the qualifications and requirements for this position
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex gap-2">
              <Input
                placeholder="Enter a requirement"
                value={currentRequirement}
                onChange={(e) => setCurrentRequirement(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddRequirement())}
              />
              <Button type="button" onClick={handleAddRequirement} className="gap-2">
                <Plus className="h-4 w-4" />
                Add
              </Button>
            </div>

            {formData.requirements.length > 0 && (
              <div className="flex flex-wrap gap-2">
                {formData.requirements.map((requirement) => (
                  <Badge key={requirement} variant="secondary" className="gap-1">
                    {requirement}
                    <X
                      className="h-3 w-3 cursor-pointer"
                      onClick={() => handleRemoveRequirement(requirement)}
                    />
                  </Badge>
                ))}
              </div>
            )}
            {errors.requirements && <p className="text-sm text-destructive">{errors.requirements}</p>}
          </CardContent>
        </Card>

        {/* Benefits */}
        <Card>
          <CardHeader>
            <CardTitle>Benefits</CardTitle>
            <CardDescription>
              Add the benefits and perks offered with this position
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex gap-2">
              <Input
                placeholder="Enter a benefit"
                value={currentBenefit}
                onChange={(e) => setCurrentBenefit(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddBenefit())}
              />
              <Button type="button" onClick={handleAddBenefit} className="gap-2">
                <Plus className="h-4 w-4" />
                Add
              </Button>
            </div>

            {formData.benefits.length > 0 && (
              <div className="flex flex-wrap gap-2">
                {formData.benefits.map((benefit) => (
                  <Badge key={benefit} variant="secondary" className="gap-1">
                    {benefit}
                    <X
                      className="h-3 w-3 cursor-pointer"
                      onClick={() => handleRemoveBenefit(benefit)}
                    />
                  </Badge>
                ))}
              </div>
            )}
            {errors.benefits && <p className="text-sm text-destructive">{errors.benefits}</p>}
          </CardContent>
        </Card>

        {/* Submit Buttons */}
        <div className="flex items-center gap-4">
          <Button type="submit" disabled={isLoading} className="min-w-32">
            {isLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
            Post Job
          </Button>
          <Button
            type="button"
            variant="outline"
            onClick={handleSaveAsDraft}
            disabled={isLoading}
          >
            Save as Draft
          </Button>
          <Button
            type="button"
            variant="ghost"
            onClick={() => navigate('/jobs')}
            disabled={isLoading}
          >
            Cancel
          </Button>
        </div>
      </form>
    </div>
  );
}

export default CreateJobPage;

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Loader2, Plus, X, Upload, Image as ImageIcon } from 'lucide-react';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';

import { CreateProviderForm } from '../types';
import { sanitize } from '../lib/auth';
import useCRUD from '../hooks/useCRUD';
import api from '../lib/api';

// Mock categories for selection
const MOCK_CATEGORIES = [
  { id: '1', name: 'Marine Equipment' },
  { id: '2', name: 'Boat Maintenance' },
  { id: '3', name: 'Electronics & Navigation' },
  { id: '4', name: 'Repair Services' },
  { id: '5', name: 'Marina Services' },
];

export function CreateProviderPage() {
  const navigate = useNavigate();
  const { toast } = useToast();

  const [formData, setFormData] = useState<CreateProviderForm>({
    name: '',
    email: '',
    phone: '',
    location: '',
    description: '',
    services: [],
    categoryIds: [],
    website: '',
    socialLinks: {
      linkedin: '',
      facebook: '',
      twitter: '',
    },
  });

  const [uploadedPhotos, setUploadedPhotos] = useState<string[]>([]);

  const [currentService, setCurrentService] = useState('');
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Use CRUD hook for provider creation
  const { createItem, creating } = useCRUD<any>({
    resource: 'providers',
    api: api.providers,
    messages: {
      created: 'Provider created successfully and is pending approval',
    },
  });

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    // Basic validation
    if (!formData.name.trim()) newErrors.name = 'Provider name is required';
    if (!formData.email.trim()) newErrors.email = 'Email is required';
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) newErrors.email = 'Invalid email format';
    if (!formData.phone.trim()) newErrors.phone = 'Phone number is required';
    if (!formData.location.trim()) newErrors.location = 'Location is required';
    if (!formData.description.trim()) newErrors.description = 'Description is required';
    if (formData.categoryIds.length === 0) newErrors.categoryIds = 'At least one category is required';
    if (formData.services.length === 0) newErrors.services = 'At least one service is required';

    // URL validation
    if (formData.website && !isValidUrl(formData.website)) {
      newErrors.website = 'Invalid website URL';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidUrl = (url: string): boolean => {
    try {
      new URL(url.startsWith('http') ? url : `https://${url}`);
      return true;
    } catch {
      return false;
    }
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleSocialLinkChange = (platform: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      socialLinks: {
        ...prev.socialLinks,
        [platform]: value,
      },
    }));
  };

  const handleAddService = () => {
    if (currentService.trim() && !formData.services.includes(currentService.trim())) {
      setFormData(prev => ({
        ...prev,
        services: [...prev.services, currentService.trim()],
      }));
      setCurrentService('');
      if (errors.services) {
        setErrors(prev => ({ ...prev, services: '' }));
      }
    }
  };

  const handleRemoveService = (service: string) => {
    setFormData(prev => ({
      ...prev,
      services: prev.services.filter(s => s !== service),
    }));
  };

  const handleCategoryToggle = (categoryId: string) => {
    setFormData(prev => ({
      ...prev,
      categoryIds: prev.categoryIds.includes(categoryId)
        ? prev.categoryIds.filter(id => id !== categoryId)
        : [...prev.categoryIds, categoryId],
    }));
    if (errors.categoryIds) {
      setErrors(prev => ({ ...prev, categoryIds: '' }));
    }
  };

  const handlePhotoUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (files) {
      Array.from(files).forEach(file => {
        const reader = new FileReader();
        reader.onload = (e) => {
          if (e.target?.result) {
            setUploadedPhotos(prev => [...prev, e.target!.result as string]);
          }
        };
        reader.readAsDataURL(file);
      });
    }
  };

  const handleRemovePhoto = (index: number) => {
    setUploadedPhotos(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    // Sanitize data
    const sanitizedData = {
      companyName: formData.name.trim(),
      contactName: formData.name.trim(), // Using same name for contact
      email: sanitize.email(formData.email),
      phone: sanitize.phone(formData.phone),
      description: formData.description.trim(),
      address: {
        street: '',
        city: formData.location.split(',')[0]?.trim() || '',
        state: formData.location.split(',')[1]?.trim() || '',
        country: 'USA',
        zipCode: ''
      },
      location: {
        coordinates: [0, 0] // Default coordinates
      },
      website: formData.website ? sanitize.url(formData.website) : undefined,
      services: formData.services,
      categoryIds: formData.categoryIds,
      socialLinks: formData.socialLinks,
    };

    const result = await createItem(sanitizedData);

    if (result) {
      navigate('/providers');
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center gap-4">
        <Button
          variant="ghost"
          size="sm"
          onClick={() => navigate('/providers')}
          className="gap-2"
        >
          <ArrowLeft className="h-4 w-4" />
          Back to Providers
        </Button>
        <div>
          <h1 className="text-3xl font-bold">Create Provider</h1>
          <p className="text-muted-foreground">Add a new marine service provider</p>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Basic Information */}
        <Card>
          <CardHeader>
            <CardTitle>Basic Information</CardTitle>
            <CardDescription>
              Enter the basic details for the provider
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="name">Provider Name *</Label>
                <Input
                  id="name"
                  placeholder="Enter provider name"
                  value={formData.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  className={errors.name ? 'border-destructive' : ''}
                />
                {errors.name && <p className="text-sm text-destructive">{errors.name}</p>}
              </div>

              <div className="space-y-2">
                <Label htmlFor="email">Email Address *</Label>
                <Input
                  id="email"
                  type="email"
                  placeholder="Enter email address"
                  value={formData.email}
                  onChange={(e) => handleInputChange('email', e.target.value)}
                  className={errors.email ? 'border-destructive' : ''}
                />
                {errors.email && <p className="text-sm text-destructive">{errors.email}</p>}
              </div>

              <div className="space-y-2">
                <Label htmlFor="phone">Phone Number *</Label>
                <Input
                  id="phone"
                  placeholder="Enter phone number"
                  value={formData.phone}
                  onChange={(e) => handleInputChange('phone', e.target.value)}
                  className={errors.phone ? 'border-destructive' : ''}
                />
                {errors.phone && <p className="text-sm text-destructive">{errors.phone}</p>}
              </div>

              <div className="space-y-2">
                <Label htmlFor="location">Location *</Label>
                <Input
                  id="location"
                  placeholder="Enter location"
                  value={formData.location}
                  onChange={(e) => handleInputChange('location', e.target.value)}
                  className={errors.location ? 'border-destructive' : ''}
                />
                {errors.location && <p className="text-sm text-destructive">{errors.location}</p>}
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">Description *</Label>
              <Textarea
                id="description"
                placeholder="Describe the provider's services and expertise"
                rows={4}
                value={formData.description}
                onChange={(e) => handleInputChange('description', e.target.value)}
                className={errors.description ? 'border-destructive' : ''}
              />
              {errors.description && <p className="text-sm text-destructive">{errors.description}</p>}
            </div>

            <div className="space-y-2">
              <Label htmlFor="website">Website (optional)</Label>
              <Input
                id="website"
                placeholder="https://example.com"
                value={formData.website}
                onChange={(e) => handleInputChange('website', e.target.value)}
                className={errors.website ? 'border-destructive' : ''}
              />
              {errors.website && <p className="text-sm text-destructive">{errors.website}</p>}
            </div>
          </CardContent>
        </Card>

        {/* Services */}
        <Card>
          <CardHeader>
            <CardTitle>Services</CardTitle>
            <CardDescription>
              Add the services this provider offers
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex gap-2">
              <Input
                placeholder="Enter a service"
                value={currentService}
                onChange={(e) => setCurrentService(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddService())}
              />
              <Button type="button" onClick={handleAddService} className="gap-2">
                <Plus className="h-4 w-4" />
                Add
              </Button>
            </div>

            {formData.services.length > 0 && (
              <div className="flex flex-wrap gap-2">
                {formData.services.map((service) => (
                  <Badge key={service} variant="secondary" className="gap-1">
                    {service}
                    <X
                      className="h-3 w-3 cursor-pointer"
                      onClick={() => handleRemoveService(service)}
                    />
                  </Badge>
                ))}
              </div>
            )}
            {errors.services && <p className="text-sm text-destructive">{errors.services}</p>}
          </CardContent>
        </Card>

        {/* Categories */}
        <Card>
          <CardHeader>
            <CardTitle>Categories</CardTitle>
            <CardDescription>
              Select the categories that best describe this provider
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
              {MOCK_CATEGORIES.map((category) => (
                <div
                  key={category.id}
                  className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                    formData.categoryIds.includes(category.id)
                      ? 'bg-primary text-primary-foreground border-primary'
                      : 'bg-card hover:bg-muted border-border'
                  }`}
                  onClick={() => handleCategoryToggle(category.id)}
                >
                  <div className="text-sm font-medium">{category.name}</div>
                </div>
              ))}
            </div>
            {errors.categoryIds && <p className="text-sm text-destructive mt-2">{errors.categoryIds}</p>}
          </CardContent>
        </Card>

        {/* Social Links */}
        <Card>
          <CardHeader>
            <CardTitle>Social Links (optional)</CardTitle>
            <CardDescription>
              Add social media profiles for the provider
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="space-y-2">
                <Label htmlFor="linkedin">LinkedIn</Label>
                <Input
                  id="linkedin"
                  placeholder="LinkedIn username or URL"
                  value={formData.socialLinks?.linkedin || ''}
                  onChange={(e) => handleSocialLinkChange('linkedin', e.target.value)}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="facebook">Facebook</Label>
                <Input
                  id="facebook"
                  placeholder="Facebook username or URL"
                  value={formData.socialLinks?.facebook || ''}
                  onChange={(e) => handleSocialLinkChange('facebook', e.target.value)}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="twitter">Twitter</Label>
                <Input
                  id="twitter"
                  placeholder="Twitter username or URL"
                  value={formData.socialLinks?.twitter || ''}
                  onChange={(e) => handleSocialLinkChange('twitter', e.target.value)}
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Photos */}
        <Card>
          <CardHeader>
            <CardTitle>Photos</CardTitle>
            <CardDescription>
              Upload photos to showcase the provider's work and facilities
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="border-2 border-dashed border-muted-foreground/25 rounded-lg p-6">
              <div className="text-center space-y-4">
                <div className="mx-auto h-12 w-12 text-muted-foreground">
                  <Upload className="h-full w-full" />
                </div>
                <div className="space-y-2">
                  <h3 className="text-lg font-semibold">Upload Photos</h3>
                  <p className="text-sm text-muted-foreground">
                    Choose multiple photos to showcase the provider's services
                  </p>
                </div>
                <div>
                  <label htmlFor="photo-upload">
                    <Button type="button" variant="outline" className="cursor-pointer">
                      <ImageIcon className="mr-2 h-4 w-4" />
                      Select Photos
                    </Button>
                    <input
                      id="photo-upload"
                      type="file"
                      multiple
                      accept="image/*"
                      onChange={handlePhotoUpload}
                      className="hidden"
                    />
                  </label>
                </div>
              </div>
            </div>

            {uploadedPhotos.length > 0 && (
              <div className="space-y-3">
                <h4 className="text-sm font-medium">Uploaded Photos ({uploadedPhotos.length})</h4>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  {uploadedPhotos.map((photo, index) => (
                    <div key={index} className="relative group">
                      <img
                        src={photo}
                        alt={`Upload ${index + 1}`}
                        className="w-full h-24 object-cover rounded-lg border"
                      />
                      <Button
                        type="button"
                        size="sm"
                        variant="destructive"
                        className="absolute -top-2 -right-2 h-6 w-6 p-0 opacity-0 group-hover:opacity-100 transition-opacity"
                        onClick={() => handleRemovePhoto(index)}
                      >
                        <X className="h-3 w-3" />
                      </Button>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Submit Buttons */}
        <div className="flex items-center gap-4">
          <Button type="submit" disabled={creating} className="min-w-32">
            {creating && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
            Create Provider
          </Button>
          <Button
            type="button"
            variant="outline"
            onClick={() => navigate('/providers')}
            disabled={creating}
          >
            Cancel
          </Button>
        </div>
      </form>
    </div>
  );
}

export default CreateProviderPage;

// Dashboard page for Marine-Axis Admin Panel
import React, { useState, useEffect } from 'react';
import {
  Building2,
  Briefcase,
  FileText,
  Users,
  TrendingUp,
  Clock,
  CheckCircle,
  AlertCircle,
  Download,
} from 'lucide-react';

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';

import { useAuth } from '../context/AuthContext';
import { Analytics, Provider, Job, Blog, Approval, User } from '../types';
import api from '../lib/api';
import { useToast } from '@/hooks/use-toast';

// Default analytics state to prevent undefined errors
const DEFAULT_ANALYTICS_STATE: Analytics = {
  totalProviders: 0,
  totalJobs: 0,
  totalBlogs: 0,
  totalUsers: 0,
  pendingApprovals: 0,
  activeJobs: 0,
  featuredProviders: 0,
  publishedBlogs: 0,
  monthlyStats: {
    providers: 0,
    jobs: 0,
    blogs: 0,
    applications: 0,
  },
  categoryStats: [],
  topLocations: [],
  recentActivity: [],
  error: '',
};

interface StatCard {
  title: string;
  value: string | number;
  change: string;
  trend: 'up' | 'down' | 'neutral';
  icon: React.ElementType;
  color: string;
}

const formatLocation = (loc: unknown): string => {
  if (!loc) return 'N/A';
  if (typeof loc === 'string') return loc;
  if (typeof loc === 'object') {
    const obj = loc as { coordinates?: unknown; city?: unknown };
    if (Array.isArray(obj.coordinates)) return obj.coordinates.join(', ');
    if (typeof obj.city === 'string') return obj.city;
  }
  return 'N/A';
};

export function DashboardPage() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();

  const [analytics, setAnalytics] = useState<Analytics>(DEFAULT_ANALYTICS_STATE);
  const [recentProviders, setRecentProviders] = useState<Provider[]>([]);
  const [recentJobs, setRecentJobs] = useState<Job[]>([]);
  const [pendingApprovals, setPendingApprovals] = useState<Approval[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!isAuthenticated || user?.role !== 'admin') return;
    const asProvider = (raw: unknown): Provider => {
      const obj = raw as Record<string, unknown>;
      return {
        id: String(obj.id || obj._id || ''),
        name: typeof obj.name === 'string' ? obj.name : 'Unnamed',
        email: typeof obj.email === 'string' ? obj.email : '',
        phone: typeof obj.phone === 'string' ? obj.phone : '',
        location: formatLocation(obj.location),
        description: typeof obj.description === 'string' ? obj.description : '',
        services: Array.isArray(obj.services) ? obj.services as string[] : [],
        categoryIds: Array.isArray(obj.categoryIds) ? obj.categoryIds as string[] : [],
        status: (['pending','approved','rejected','suspended'].includes(String(obj.status))) ? obj.status as Provider['status'] : 'pending',
        featured: Boolean(obj.featured),
        logo: typeof obj.logo === 'string' ? obj.logo : undefined,
        images: Array.isArray(obj.images) ? obj.images as string[] : [],
        website: typeof obj.website === 'string' ? obj.website : undefined,
        socialLinks: (typeof obj.socialLinks === 'object' && obj.socialLinks) ? obj.socialLinks as { linkedin?: string; facebook?: string; twitter?: string; } : {},
        verificationDocuments: Array.isArray(obj.verificationDocuments) ? obj.verificationDocuments as string[] : [],
        rating: typeof obj.rating === 'number' ? obj.rating : 0,
        reviewCount: typeof obj.reviewCount === 'number' ? obj.reviewCount : 0,
        createdAt: typeof obj.createdAt === 'string' ? obj.createdAt : new Date().toISOString(),
        updatedAt: typeof obj.updatedAt === 'string' ? obj.updatedAt : (typeof obj.createdAt === 'string' ? obj.createdAt : new Date().toISOString()),
      };
    };
    const asJob = (raw: unknown): Job => {
      const obj = raw as Record<string, unknown>;
      return {
        id: String(obj.id || obj._id || ''),
        title: typeof obj.title === 'string' ? obj.title : 'Untitled Job',
        description: typeof obj.description === 'string' ? obj.description : '',
        providerId: typeof obj.providerId === 'string' ? obj.providerId : '',
        provider: obj.provider as Provider | undefined,
        categoryIds: Array.isArray(obj.categoryIds) ? obj.categoryIds as string[] : [],
        location: formatLocation(obj.location),
        salaryRange: (typeof obj.salaryRange === 'object' && obj.salaryRange) ? obj.salaryRange as Job['salaryRange'] : { min: 0, max: 0, currency: 'USD' },
        requirements: Array.isArray(obj.requirements) ? obj.requirements as string[] : [],
        benefits: Array.isArray(obj.benefits) ? obj.benefits as string[] : [],
        type: (['full-time','part-time','contract','internship'].includes(String(obj.type))) ? obj.type as Job['type'] : 'full-time',
        remote: Boolean(obj.remote),
        urgency: (['low','medium','high'].includes(String(obj.urgency))) ? obj.urgency as Job['urgency'] : 'low',
        status: (['draft','published','closed','paused'].includes(String(obj.status))) ? obj.status as Job['status'] : 'draft',
        expiresAt: typeof obj.expiresAt === 'string' ? obj.expiresAt : new Date().toISOString(),
        applications: typeof obj.applications === 'number' ? obj.applications : 0,
        createdAt: typeof obj.createdAt === 'string' ? obj.createdAt : new Date().toISOString(),
        updatedAt: typeof obj.updatedAt === 'string' ? obj.updatedAt : (typeof obj.createdAt === 'string' ? obj.createdAt : new Date().toISOString()),
      };
    };
    const asApproval = (raw: unknown): Approval => {
      const obj = raw as Record<string, unknown>;
      return {
        id: String(obj.id || obj._id || ''),
        type: (['provider_registration','provider_edit','job_posting','blog_post'].includes(String(obj.type))) ? obj.type as Approval['type'] : 'provider_registration',
        entityId: typeof obj.entityId === 'string' ? obj.entityId : '',
        entity: obj.entity as Provider | Job | Blog | undefined,
        changes: (typeof obj.changes === 'object' && obj.changes) ? obj.changes as Record<string, unknown> : {},
        requestedBy: typeof obj.requestedBy === 'string' ? obj.requestedBy : '',
        requester: obj.requester as User | Provider | undefined,
        status: (['pending','approved','rejected'].includes(String(obj.status))) ? obj.status as Approval['status'] : 'pending',
        priority: (['low','medium','high'].includes(String(obj.priority))) ? obj.priority as Approval['priority'] : 'low',
        reviewerId: typeof obj.reviewerId === 'string' ? obj.reviewerId : undefined,
        reviewer: obj.reviewer as User | undefined,
        reviewNotes: typeof obj.reviewNotes === 'string' ? obj.reviewNotes : undefined,
        metadata: (typeof obj.metadata === 'object' && obj.metadata) ? obj.metadata as Record<string, unknown> : {},
        createdAt: typeof obj.createdAt === 'string' ? obj.createdAt : new Date().toISOString(),
        updatedAt: typeof obj.updatedAt === 'string' ? obj.updatedAt : (typeof obj.createdAt === 'string' ? obj.createdAt : new Date().toISOString()),
      };
    };
    // Type for admin dashboard response
    interface AdminDashboardData {
      providers?: { total?: number };
      customers?: { total?: number };
      admins?: { total?: number };
      pendingProviders?: unknown[];
      // ...add more fields as needed
    }
    const run = async () => {
      setLoading(true);
      try {
        const adminDash = await api.analytics.adminDashboard();
        if (adminDash.success) {
          const dashData = adminDash.data as AdminDashboardData;
          setAnalytics(prev => ({
            ...prev,
            totalProviders: dashData.providers?.total ?? prev.totalProviders,
            totalUsers: (dashData.customers?.total ?? 0) + (dashData.providers?.total ?? 0) + (dashData.admins?.total ?? 0),
            pendingApprovals: Array.isArray(dashData.pendingProviders) ? dashData.pendingProviders.length : prev.pendingApprovals,
          }));
          if (Array.isArray(dashData.pendingProviders)) {
            const normalized = (dashData.pendingProviders as unknown[]).map(asProvider) as Provider[];
            setRecentProviders(normalized.slice(0,5));
          }
        }
        const providersResponse = await api.providers.list({ limit: 5, sortBy: 'createdAt', sortOrder: 'desc' });
        if (providersResponse.success) {
          const rawArr = Array.isArray((providersResponse as unknown as { data?: { data: unknown[] } }).data?.data)
            ? (providersResponse as unknown as { data?: { data: unknown[] } }).data!.data
            : (Array.isArray((providersResponse as { data: unknown }).data) ? (providersResponse as { data: unknown[] }).data : []);
          const normalized = (rawArr as unknown[]).map(asProvider) as Provider[];
          setRecentProviders(normalized);
        }
        const jobsResponse = await api.jobs.list({ limit: 5, sortBy: 'createdAt', sortOrder: 'desc' });
        if (jobsResponse.success) {
          const rawArr = Array.isArray((jobsResponse as unknown as { data?: { data: unknown[] } }).data?.data)
            ? (jobsResponse as unknown as { data?: { data: unknown[] } }).data!.data
            : (Array.isArray((jobsResponse as { data: unknown }).data) ? (jobsResponse as { data: unknown[] }).data : []);
          const normalized = (rawArr as unknown[]).map(asJob) as Job[];
          setRecentJobs(normalized);
        }
        if (api.approvals?.getPending) {
          // Approvals endpoint not implemented in backend; skipping fetch to avoid 404 spam.
          // const approvalsResponse = await api.approvals.getPending({ limit: 10 });
          // if (approvalsResponse.success) { /* normalization removed */ }
          setPendingApprovals([]);
        }
      } catch (error: unknown) {
        let message = 'Failed to load dashboard data';
        let status = '';
        if (error instanceof Error) {
          message = error.message;
        }
        if (typeof error === 'object' && error && 'response' in error) {
          // @ts-expect-error
          status = error.response?.status;
        }
        toast({ title: 'Dashboard Error', description: `${message}${status ? ` (Status: ${status})` : ''}`, variant: 'destructive' });
        setAnalytics(prev => ({ ...prev, error: `${message}${status ? ` (Status: ${status})` : ''}` }));
      } finally {
        setLoading(false);
      }
    };
    run();
  }, [isAuthenticated, user, toast]);

  // Show error in UI if present
  if (analytics.error) {
    return <div className="p-8 text-center text-destructive">{analytics.error}</div>;
  }

  const handleExportData = async () => {
    try {
      const response = await api.analytics.export('dashboard');
      if (response.success) {
        toast({
          title: 'Export Started',
          description: 'Your data export has been queued for download.',
        });
      }
    } catch (error: unknown) {
      const message = error instanceof Error ? error.message : 'Failed to export data';
      toast({
        title: 'Export Error',
        description: message,
        variant: 'destructive',
      });
    }
  };

  const getStatCards = (): StatCard[] => {

    return [
      {
        title: 'Total Providers',
        value: analytics.totalProviders ?? 0,
        change: `+${analytics.monthlyStats?.providers ?? 0} this month`,
        trend: 'up',
        icon: Building2,
        color: 'text-primary',
      },
      {
        title: 'Active Jobs',
        value: analytics.activeJobs ?? 0,
        change: `+${analytics.monthlyStats?.jobs ?? 0} this month`,
        trend: 'up',
        icon: Briefcase,
        color: 'text-success',
      },
      {
        title: 'Published Blogs',
        value: analytics.publishedBlogs ?? 0,
        change: `+${analytics.monthlyStats?.blogs ?? 0} this month`,
        trend: 'up',
        icon: FileText,
        color: 'text-accent',
      },
      {
        title: 'Pending Approvals',
        value: analytics.pendingApprovals ?? 0,
        change: 'Require attention',
        trend: (analytics.pendingApprovals ?? 0) > 10 ? 'up' : 'neutral',
        icon: Clock,
        color: (analytics.pendingApprovals ?? 0) > 10 ? 'text-warning' : 'text-muted-foreground',
      },
    ];
  };

  const getTrendIcon = (trend: 'up' | 'down' | 'neutral') => {
    if (trend === 'up') return <TrendingUp className="h-4 w-4" />;
    if (trend === 'down') return <TrendingUp className="h-4 w-4 rotate-180" />;
    return null;
  };

  const getApprovalTypeLabel = (type: string) => {
    switch (type) {
      case 'provider_registration': return 'Provider Registration';
      case 'provider_edit': return 'Provider Edit';
      case 'job_posting': return 'Job Posting';
      case 'blog_post': return 'Blog Post';
      default: return type;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'destructive';
      case 'medium': return 'warning';
      case 'low': return 'secondary';
      default: return 'secondary';
    }
  };

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold">Dashboard</h1>
            <p className="text-muted-foreground">Overview of your marine services platform</p>
          </div>
        </div>

        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
          {[...Array(4)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <div className="h-4 bg-muted rounded w-24"></div>
                <div className="h-4 w-4 bg-muted rounded"></div>
              </CardHeader>
              <CardContent>
                <div className="h-8 bg-muted rounded w-16 mb-2"></div>
                <div className="h-3 bg-muted rounded w-32"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (!isAuthenticated && !isLoading) {
    return <div className="p-8 text-center text-destructive">You must be logged in as an admin to view the dashboard.</div>;
  }
  if (user && user.role !== 'admin') {
    return <div className="p-8 text-center text-destructive">Access denied. Admins only.</div>;
  }

  const statCards = [
    {
      title: 'Total Providers',
      value: analytics.totalProviders ?? recentProviders.length,
      change: `+${analytics.monthlyStats.providers ?? 0} this month`,
      trend: 'up',
      icon: Building2,
      color: 'text-primary',
    },
    {
      title: 'Active Jobs',
      value: analytics.activeJobs ?? recentJobs.filter(j => j.status === 'published').length,
      change: `+${analytics.monthlyStats.jobs ?? 0} this month`,
      trend: 'up',
      icon: Briefcase,
      color: 'text-success',
    },
    {
      title: 'Published Blogs',
      value: analytics.publishedBlogs ?? 0,
      change: `+${analytics.monthlyStats.blogs ?? 0} this month`,
      trend: 'up',
      icon: FileText,
      color: 'text-accent',
    },
    {
      title: 'Pending Approvals',
      value: analytics.pendingApprovals ?? pendingApprovals.length,
      change: 'Require attention',
      trend: (analytics.pendingApprovals ?? pendingApprovals.length) > 10 ? 'up' : 'neutral',
      icon: Clock,
      color: (analytics.pendingApprovals ?? pendingApprovals.length) > 10 ? 'text-warning' : 'text-muted-foreground',
    },
  ] as StatCard[];

  return (
    <div className="space-y-6 animate-fade-in">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">
            Welcome back, {user?.name}!
          </h1>
          <p className="text-muted-foreground">
            Here's what's happening with your marine services platform today.
          </p>
        </div>
        <Button onClick={handleExportData} variant="outline">
          <Download className="mr-2 h-4 w-4" />
          Export Data
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        {statCards.map((stat, index) => (
          <Card key={index} className="hover:shadow-medium transition-shadow">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
